catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == ' ') {
        return;
    }

    if (newValue == 'cables') {
        g_form.clearOptions('sub_category')
        g_form.addOption('sub_category', 'UTP Cable', 'UTP Cable');
        g_form.addOption('sub_category', 'STP Cable', 'STP Cable');
        g_form.addOption('sub_category', 'Coaxial Cable', 'Coaxial Cable');
        g_form.addOption('sub_category', 'Fiber Optic Cable', 'Fiber Optic Cable');
    }
    if (newValue == 'repeaters') {
        g_form.clearOptions('sub_category')
        g_form.addOption('sub_category', 'Analog', 'Analog');
        g_form.addOption('sub_category', 'Digital', 'Digital');
    }
    if (newValue == 'hub') {
        g_form.clearOptions('sub_category')
        g_form.addOption('sub_category', 'Active', 'Active');
        g_form.addOption('sub_category', 'Passive', 'Passive');
    }
    if (newValue == 'switches') {
        g_form.clearOptions('sub_category')
        g_form.addOption('sub_category', 'SPST', 'SPST');
        g_form.addOption('sub_category', 'SPDT', 'SPDT');
        g_form.addOption('sub_category', 'DPST', 'DPST');
        g_form.addOption('sub_category', 'DPDT', 'DPDT');
    }
}
