catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
if (newValue == 'UTP Cable') {
        g_form.clearOptions('sub_sub_category')
        g_form.addOption('sub_sub_category', 'd-link-utp 4-pair,', 'd-link-utp 4-pair,');
        g_form.addOption('sub_sub_category', 'Schneider- utp_4-pair', 'Schneider- utp_4-pair');

    }
    if (newValue == 'STP Cable') {
        g_form.clearOptions('sub_sub_category')
        g_form.addOption('sub_sub_category','d-link-utp 4-pair,', 'd-link-utp 4-pair,');
        g_form.addOption('sub_sub_category', 'Schneider- utp_4-pair', 'Schneider- utp_4-pair');
    }
    if (newValue == 'Coaxial Cable') {
        g_form.clearOptions('sub_sub_category')
        g_form.addOption('sub_sub_category', 'NPC SODIAL Coaxial Cable,', 'NPC SODIAL Coaxial Cable,');
        g_form.addOption('sub_sub_category', 'CABLESETC Coaxial Cable', 'CABLESETC Coaxial Cable');
    }
    if (newValue == 'Fiber Optic Cable') {
        g_form.clearOptions('sub_sub_category')
        g_form.addOption('sub_sub_category', 'Belkin 3M Duplex Fiber Optic Cable', 'Belkin 3M Duplex Fiber Optic Cable');


    }
}
