sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Ensure the requester field has changed and has a value
    if (current.requester.changes() && current.requester) {
        var requesterGR = new GlideRecord('sys_user');

        if (requesterGR.get(current.requester)) {
            // Initialize variables to store requester information
            var company = 'N/A';
            var email = requesterGR.email || 'N/A';
            var mobilePhone = requesterGR.mobile_phone || 'N/A';
            var department = 'N/A';
            var title = requesterGR.title || 'N/A';

            // Fetch the company name (company is a reference field, so we need to query it)
            if (requesterGR.company) {
                var companyGR = new GlideRecord('core_company'); 
                if (companyGR.get(requesterGR.company)) {
                    company = companyGR.name || 'N/A';
                }
            }

            // Fetch the department name (department is a reference field, so we need to query it)
            if (requesterGR.department) {
                var departmentGR = new GlideRecord('cmn_department'); 
                if (departmentGR.get(requesterGR.department)) {
                    department = departmentGR.name || 'N/A';
                }
            }

            // Create the additional information string
            var additionalInfo = 
                'Requester Company: ' + company + '\n' +
                'Requester Email: ' + email + '\n' +
                'Requester Mobile Phone: ' + mobilePhone + '\n' +
                'Requester Department: ' + department + '\n' +
                'Requester Title: ' + title;

            // Concatenate with the existing description
            current.description = (current.description || '') + '\n' + additionalInfo;
        }
    }
})(current, previous);
