sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    // Check if the form is loading or if there's no new value
    if (isLoading || newValue === '') {
        return;
    }

    // Get the GlideRecord for the requested_for field (sys_user table)
    var requesterGR = new GlideRecord('sys_user');
    if (requesterGR.get(newValue)) {
        // Initialize variables to store requester information
        var company = '';
        var email = requesterGR.email;
        var mobilePhone = requesterGR.mobile_phone;
        var department = '';
        var title = requesterGR.title;

        // Fetch the company name (company is a reference field, so we need to query it)
        if (requesterGR.company) {
            var companyGR = new GlideRecord('core_company'); 
            if (companyGR.get(requesterGR.company)) {
                company = companyGR.name;
            }
        }

        // Fetch the department name (department is a reference field, so we need to query it)
        if (requesterGR.department) {
            var departmentGR = new GlideRecord('cmn_department'); 
            if (departmentGR.get(requesterGR.department)) {
                department = departmentGR.name;
            }
        }


        var additionalInfo = 
            'Requester Company: ' + (company || 'N/A') + '\n' +
            'Requester Email: ' + (email || 'N/A') + '\n' +
            'Requester Mobile Phone: ' + (mobilePhone || 'N/A') + '\n' +
            'Requester Department: ' + (department || 'N/A') + '\n' +
            'Requester Title: ' + (title || 'N/A');

        // Concatenate with existing description
        var currentDescription = g_form.getValue('description');
        g_form.setValue('description', currentDescription + '\n' + additionalInfo);
    }
}
