sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Use g_scratchpad values to populate description
    if (typeof g_scratchpad !== 'undefined') {
        var additionalInfo = 
            'Requester Company: ' + (g_scratchpad.company || 'N/A') + '\n' +
            'Requester Email: ' + (g_scratchpad.email || 'N/A') + '\n' +
            'Requester Mobile Phone: ' + (g_scratchpad.mobile_phone || 'N/A') + '\n' +
            'Requester Department: ' + (g_scratchpad.department || 'N/A') + '\n' +
            'Requester Title: ' + (g_scratchpad.title || 'N/A');

        var currentDescription = g_form.getValue('description') || '';
        g_form.setValue('description', currentDescription + '\n' + additionalInfo);
    }
}
