sys_script_include.script:
var FetchRequesterDetails = Class.create();
FetchRequesterDetails.prototype = {
    initialize: function() {},

    getRequesterDetails: function(requesterSysId) {
        var result = {
            company: 'N/A',
            email: 'N/A',
            mobile_phone: 'N/A',
            department: 'N/A',
            title: 'N/A'
        };

        var requesterGR = new GlideRecord('sys_user');
        if (requesterGR.get(requesterSysId)) {
            result.email = requesterGR.email || 'N/A';
            result.mobile_phone = requesterGR.mobile_phone || 'N/A';
            result.title = requesterGR.title || 'N/A';

            // Fetch company name
            if (requesterGR.company) {
                var companyGR = new GlideRecord('core_company');
                if (companyGR.get(requesterGR.company)) {
                    result.company = companyGR.name || 'N/A';
                }
            }

            // Fetch department name
            if (requesterGR.department) {
                var departmentGR = new GlideRecord('cmn_department');
                if (departmentGR.get(requesterGR.department)) {
                    result.department = departmentGR.name || 'N/A';
                }
            }
        }

        return JSON.stringify(result);
    },

    type: 'FetchRequesterDetails'
};
