sys_ui_action.script:
(function executeAction() {
    // Define variables to insert record values in the email body
    var requester = current.requester.getDisplayValue();
    var number = current.number;
    var state = current.state.getDisplayValue();
    var shortDescription = current.short_description;
    var product = current.products.getDisplayValue();
    var assignmentGroup = current.assignment_group.getDisplayValue();
    var assignedTo = current.assigned_to.getDisplayValue();

    // Construct the HTML table for the email body
    var emailBody = 
        "<p>Hi " + requester + ",</p>" +
        "<p>Following are the current status and update on your query " + number + ":</p>" +
        "<table style='border-collapse: collapse; width: 100%;' border='1'>" +
            "<colgroup>" +
                "<col style='width: 50%;'>" +
                "<col style='width: 50%;'>" +
            "</colgroup>" +
            "<tbody>" +
                "<tr><td style='padding: 5px; text-align: left;'>Number</td><td style='padding: 5px;'>" + number + "</td></tr>" +
                "<tr><td style='padding: 5px; text-align: left;'>State</td><td style='padding: 5px;'>" + state + "</td></tr>" +
                "<tr><td style='padding: 5px; text-align: left;'>Short Description</td><td style='padding: 5px;'>" + shortDescription + "</td></tr>" +
                "<tr><td style='padding: 5px; text-align: left;'>Product</td><td style='padding: 5px;'>" + product + "</td></tr>" +
                "<tr><td style='padding: 5px; text-align: left;'>Assignment Group</td><td style='padding: 5px;'>" + assignmentGroup + "</td></tr>" +
                "<tr><td style='padding: 5px; text-align: left;'>Assigned To</td><td style='padding: 5px;'>" + assignedTo + "</td></tr>" +
            "</tbody>" +
        "</table>" +
        "<p>Regards,</p>" +
        "<p>ChromaTech help desk team.</p>";

    // Trigger the email event, passing the requesterâ€™s email and HTML content as parameters
    gs.eventQueue('custom.email.send', current, current.requester.email, emailBody);

    // Redirect back to the current record
    action.setRedirectURL(current);

    // Display a confirmation message
    gs.addInfoMessage('Email sent successfully to the requester.');
})();
